import React from 'react';
import Navegation from './components/Navegation/Navegation';
import PostCardList from './components/PostCardList/PostCardList'
import Signin from './components/Signin/Signin';
import Register from './components/Register/Register';
import './App.css';

class App extends React.Component{
  constructor(){
    super()
    this.state = {
      isSignIn: false,
      route: 'signin',
      cards : []
    }
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/posts')
    .then(response => response.json())
    .then(data => this.setState({cards: data}))
    console.log(this.state.isSignIn)
  }

  isSignInChange = () => {
    this.state.isSignIn === false 
    ?
    this.setState({route: true})
    // console.log(this.state.isSignIn)
    :
    this.setState({route:false})
    // console.log(this.state.isSignIn)
  }

  onRouteChange = (route) =>{

    this.setState({route:route})
  }


  render() {
    const {route} = this.state;
    if(route === 'signin'){
      return(
        <div>
          <Navegation onRouteChange ={this.onRouteChange} isSignedIn = {this.state.isSignIn}/>
          <Signin isSignInChange= {this.isSignInChange} onRouteChange = {this.onRouteChange}/>
        </div>
      )
    }else if (route === 'register'){
      return(
        <div>
          <Navegation onRouteChange ={this.onRouteChange} isSignedIn = {this.state.isSignIn}/>
          <Register onRouteChange = {this.onRouteChange}/>
        </div>
      )
    }else if(route === 'mealposts'){
      return(
        <div>
          <Navegation onRouteChange ={this.onRouteChange} isSignedIn = {this.state.isSignIn}/>
          <PostCardList cards={this.state.cards}/>
        </div>
      )
    }else if(route === 'postedbyme'){
      return(
        <div>
          <Navegation onRouteChange ={this.onRouteChange} isSignedIn = {this.state.isSignIn}/>
          <PostCardList cards={this.state.cards}/>
        </div>
      )
    }else if(route === 'reserved'){
      return(
        <div>
          <Navegation onRouteChange ={this.onRouteChange} isSignedIn = {this.state.isSignIn}/>
          <PostCardList cards={this.state.cards}/>
        </div>
      )
    }else{
      return(
        <div>
          <Navegation onRouteChange ={this.onRouteChange} isSignedIn = {this.state.isSignIn}/>
        </div>
      )
    }
  }  
}

export default App;
