import React from 'react';
import Navegation from './components/Navegation/Navegation';
import PostCardList from './components/PostCardList/PostCardList'
import Signin from './components/Signin/Signin';
import Register from './components/Register/Register';
import CreateMeal from './components/CreateMeal/CreateMeal'
import './App.css';

class App extends React.Component{
  constructor(){
    super()
    this.state = {
      isSignIn: false,
      route: 'signin',
      cards : [],
      reserved : [],
      user: {
        name:'',
        lastname: '',
        email: ''
      }
    }
  }

  loadUser = (user) => {
    this.setState({user: {
      name: user.name,
      lastname: user.lastname,
      email: user.email
    }})
    console.log(this.state.user.email)

  }

  updateMealPost = () => {
    fetch('http://localhost:3000/meals')
    .then(response => response.json())
    .then(data => this.setState({cards: data}))
    // this.isSignInChange()
    // console.log(this.state.isSignIn)
  }



  isSignInChange = () => {
    this.state.isSignIn === false 
    ?
    this.setState({isSignIn: true})
    // console.log(this.state.isSignIn)
    :
    this.setState({isSignIn:false})
    // console.log(this.state.isSignIn)
  }

  onRouteChange = (route) =>{
    if (route === 'mealposts'){
      this.updateMealPost()
      this.updateMealPost()
    }

    this.setState({route:route})

  }

  reservePlate = (index) => {

    let plateToSend = {
      id:this.state.cards[index].id,
      plateName: this.state.cards[index].platename,
      description: this.state.cards[index].description,
      email: this.state.user.email,
      image:this.state.cards[index].image,
      price: this.state.cards[index].price
    }

    fetch('http://localhost:3000/reserve', {
      method:'post',
      headers: {'Content-type': 'application/json'},
      body: JSON.stringify(plateToSend)
      })


    this.state.reserved.push(this.state.cards[index])
    this.state.cards.splice(index,1)
    this.onRouteChange('mealposts')
    // this.setState({reserved:reserved})


  }


  render() {
    const {route} = this.state;
    if(route === 'signin'){
      return(
        <div>
          <Navegation isSignInChange = {this.isSignInChange} onRouteChange ={this.onRouteChange} isSignedIn = {this.state.isSignIn}/>
          <Signin loadUser = {this.loadUser} isSignInChange= {this.isSignInChange} onRouteChange = {this.onRouteChange}/>
        </div>
      )
    }else if (route === 'register'){
      return(
        <div>
          <Navegation isSignInChange = {this.isSignInChange} onRouteChange ={this.onRouteChange} isSignedIn = {this.state.isSignIn}/>
          <Register loadUser = {this.loadUser} isSignInChange = {this.isSignInChange} onRouteChange = {this.onRouteChange}/>
        </div>
      )
    }else if(route === 'mealposts'){
      return(
        <div>
          <Navegation isSignInChange = {this.isSignInChange} onRouteChange ={this.onRouteChange} isSignedIn = {this.state.isSignIn}/>
          <PostCardList reservePlate = {this.reservePlate} cards={this.state.cards}/>
        </div>
      )
    }else if(route === 'postedbyme'){
      return(
        <div>
          <Navegation isSignInChange = {this.isSignInChange} onRouteChange ={this.onRouteChange} isSignedIn = {this.state.isSignIn}/>
          <PostCardList cards={this.state.cards}/>
        </div>
      )
    }else if(route === 'reserved'){
      return(
        <div>
          <Navegation isSignInChange = {this.isSignInChange} onRouteChange ={this.onRouteChange} isSignedIn = {this.state.isSignIn}/>
          <PostCardList cards={this.state.reserved}/>
        </div>
      )
    }else if(route === 'postameal'){
      return(
        <div>
          <Navegation isSignInChange = {this.isSignInChange} onRouteChange ={this.onRouteChange} isSignedIn = {this.state.isSignIn}/>
          <CreateMeal userEmail = {this.state.user.email} onRouteChange={this.onRouteChange} cards={this.state.cards}/>
        </div>
      )
    }else{
      return(
        <div>
          <Navegation isSignInChange = {this.isSignInChange} onRouteChange ={this.onRouteChange} isSignedIn = {this.state.isSignIn}/>
        </div>
      )
    }
  }  
}

export default App;
