import React from 'react';
import {storage} from '../firebase'
import './CreateMeal.css'


class CreateMeal extends React.Component {
	constructor(props){
		super(props)
		// this.handleChange = this.handleChange.bind(this);
		this.state = {
			image: null,
			url: ''
		}
	}

	handleChange = (event) => {
		if(event.target.files[0]){
			this.setState({image:event.target.files[0]})

		}
	}
	handleUpload = () => {
		const {image} = this.state;
		const uploadTask = storage.ref(`images/${image.name}`).put(image);
		uploadTask.on('state_changed',
			(snapshot) => {
 			//progress  

			}, 
			(error) => {
			//errors 
				console.log(error)

			}, 
			() => {
			// complete
				storage.ref('images').child(image.name).getDownloadURL().then(url => {
				this.setState({url})
			})

			})
	}


	render() {

		return(
			<div className= 'gradient body'>
				<div className = 'shadow-5 center-2 br4 pa2'>
					<div className ='tc pa3 ma3'>
						<input className='' onChange = {this.handleChange}type = 'file' />


					</div>
					<div className=''>
						<img className ='br3' width ='350px' height='150px' src = {this.state.url || 'https://via.placeholder.com/350x150'} alt='plate img' />
					</div>
					<div className='center'>
						<label className="db fw6 lh-copy f6" htmlFor="password">Pick Up Adress</label>
					    <input placeholder = 'Ex: 320 E Gun Hill Bronx NY 10467'className=" pa2 input-reset ba bg-transparent hover-bg-black w-100" type="text" />
						<label className="db fw6 lh-copy f6" htmlFor="password">Plate Name</label>
					    <input placeholder = 'Ex: Rice and beans with chicken'className=" pa2 input-reset ba bg-transparent hover-bg-black w-99" type="text" />
					    <label className="db fw6 lh-copy f6" htmlFor="password">Plate Description</label>
					    <input placeholder = 'Ex: fried chicken black beans yellow rice'className=" pa2 input-reset ba bg-transparent hover-bg-black w-100" type="text" />
					    <label className="db fw6 lh-copy f6" htmlFor="password">Plate Price</label>
					    <input placeholder = 'In $ Max price is $10'className="pa2 input-reset ba bg-transparent hover-bg-black w-100" type="number" min="1.00" step="0.50" max="10.00" />
					</div>
					<div className = 'tc'>
						<button className='f6 link dim ba bw1 ph3 pv2 mb2 dib black' onClick = {this.handleUpload}>Add Plate</button>
					</div>
				</div>
			</div>

		);
	}
}

export default CreateMeal;
