	render() {
		const {onRouteChange} = this.props;

		return( 	
			<article className="br3 ba shadow-5 b--black-10 mv4 w-100 w-50-m w-25-l mw6 center">
				<main className="pa4 black-80">
				  <div className="measure">
				    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
				      <legend className="f3 fw6 ph0 mh0">Sign In</legend>
				      <div className="mt3">
				        <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
				        <input onChange ={this.onEmailChange} className="pa2 input-reset ba bg-transparent hover-bg-black w-100" type="email" name="email-address"/>
				      </div>
				      <div className="mv3">
				        <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
				        <input onChange ={this.onPasswordChange} className="b pa2 input-reset ba bg-transparent hover-bg-black w-100" type="password" name="password" />
				      </div>
				    </fieldset>
				    <div className="">
				      <input onClick = {() => onRouteChange('home')} className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="submit" value="Sign in" />
				    </div>
				    <div className="lh-copy mt3">
				      <p onClick = {() => onRouteChange('register')} className="pointer f6 link dim black db">Register</p>
				    </div>
				  </div>
				</main>
			</article>
		);
	}
} 
